from logging import config
import streamlit as st
import pandas as pd
from streamlit_authenticator import Authenticate


#---CONFIGURATION DE LA PAGE ---: 
st.set_page_config(
    page_title="Mon application",
    layout="wide",  # ← IMPORTANT
    initial_sidebar_state="expanded"
)
#Pour faire un thème pour l'application, il faut créer un dossier.streamlit puis un fichier dedans config.toml
#Si on veut une personalisation, on peut intégrer du CSS


#---GESTION DES UTILISATEURS ET CONNECTIONS ---: 

# Lire le CSV utilisateurs :
df_utilisateurs = pd.read_csv("Comptes_utilisateurs.csv")

dictionnaire_utilisateurs={"usernames" : {}}

#Boucle pour transformer le dataframe en dictionnaire, format pour Authenticate : 

for i in range (len(df_utilisateurs)):
    #parcours les lignes du dataframe (itération sur le tableau) 
    # - On peut utiliser: for cle, valeur  in df_utilisateurs:.iterrows()
    row= df_utilisateurs.iloc[i]
    #On choisit notre clé de notre premier dictionnaire
    identifiant = row["email"]
    #On remplit notre dictionnaire imbriquée pour l'utilisateur 
    #On récupère la valeur de la colonne "name", "password", ... dans la ligne row
    dictionnaire_utilisateurs["usernames"][identifiant] = {
        "name" : row["name"] , 
        "password":row["password"]  , 
        "email" : row["email"],
        "failed_login_attempts" : row["failed_login_attempts"],
        "role" : row["role"],
    }


authenticator = Authenticate(
    dictionnaire_utilisateurs,  # Les données des comptes
    "cookie name",         # Le nom du cookie, un str quelconque
    "cookie key",          # La clé du cookie, un str quelconque
    30,                    # Le nombre de jours avant que le cookie expire
)
#st.config.set_option('theme.base', 'light')

def accueil():
      
      colonne1, colonne2, colonne3 = st.columns(3)
     

      st.sidebar.header(f"Bienvenue {st.session_state['name']}")
      choix = st.sidebar.selectbox("Menu", ["Accueil", "Animaux"])
      
      if choix =="Accueil":
          st.title("Bienvenu sur le contenu réservé aux utilisateurs connectés")
          st.image("https://www.zinfos974.com/wp-content/uploads/2020/09/49756180-38605046.jpg")
          
      if choix =="Animaux":
          st.title("Quelques animaux rigolos pour vous faire sourire")

          colonne1, colonne2, colonne3, colonne4 = st.columns(4)
          with colonne1:
              st.image("data:image/jpeg;base64,")
          with colonne2:
              st.image("data:image/jpeg;base64,")
          with colonne3:
             st.image("data:image/jpeg;base64,")
          with colonne4:             
             st.image("data:image/jpeg;base64,")

authenticator.login()          

if st.session_state["authentication_status"]:
  accueil()
  # Le bouton de déconnexion
  authenticator.logout("Déconnexion")   

elif st.session_state["authentication_status"] is False:
    st.error("L'username ou le password est/sont incorrect")

elif st.session_state["authentication_status"] is None:
    st.info('Les champs username et mot de passe doivent être remplie. Le username/mdp est utilisateur@gmail.com/utilisateurMDP')
    